package co.kukurin.test;

import co.kukurin.EditDistance;
import org.yeastrc.fasta.FASTAEntry;
import org.yeastrc.fasta.FASTAReader;

public class ReadFrom {

  public static void main(String[] args) throws Exception {
    FASTAReader reader = FASTAReader.getInstance(
        "genomes/clostridium/_clostridium_cellulosi.DG5.dna.toplevel.fa");
    FASTAEntry entry = reader.readNext();
    String sequence = entry.getSequence();

    String testAgainst =
      "GGCCGGTTCAGATAAAGCTTGTCCCGCCGAACGCCCCGTATTTTAATGGCGCACACCTGCTTCTCGCGGCGGACTGCACAGCTTACGCCTA"
          + "TGCTAACTTCCATAAAGACTTTATGAGAAACAAGATAACATTAATGGGCTGCCCGAAGTTGGATATGGTCGATTACACCGAAAAA"
          + "CTCACCGCCATCATCTCGATGAATGAGATTAAGAGCGTATCAGTCGTCAGGATGGAATTTCCATGGTGCGGAGGCATTGAACATG"
          + "CCGTCAAAGAGGCTCTCCGCGCAAGCGGCAATATGATTCCGTGGAGTGTCACCGTAATCGGCACAGACGGCACAATTATTGAACA"
          + "GTAATTTTATATAAATAAAATTTTATTTTTGGAGGAATTGCAATGTCAGAAATGTTTTGTTTTCAGTGTGAACAGACAGCTAACG"
          + "GAACAGGGTGCAAATCCGCAGGAGTATGCGGCAAAAGTGCCGCAACGTCACACGCTCAGGATGAGGTCACCTGCGCTCTGAATGC"
          + "TTTGGCCAATGCGGCAAACAAGGCGGGCAGGTCAAAGAAGACCGATACTCTTAATGTAGACGGGCTGTTTATGACCGTCACAAAC"
          + "";
    String substring = sequence.substring(1524925, 1524925 + 10000);

    int ed = EditDistance.calculate(substring, testAgainst);
    System.out.println(ed);
  }

}
